std::optional<double> safeDivide(double a, double b) {
    if (b == 0.0) {
        return std::nullopt;
    }
    return a / b;
}

int safeGetFirst(const std::vector<int>& v) {
    if (v.empty()) {
        throw std::out_of_range("Cannot get first element of empty vector");
    }
    return v.front();
}
