#include <vector>
#include <memory>
#include <mutex>

class SafeCounter {
private:
    mutable std::mutex mtx;
    int value = 0;
public:
    void increment() {
        std::lock_guard<std::mutex> lock(mtx);
        ++value;
    }
    int get() const {
        std::lock_guard<std::mutex> lock(mtx);
        return value;
    }
};

auto counter = std::make_unique<SafeCounter>();
counter->increment();
return counter->get();
